import { Component,ElementRef  } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {

  constructor(private elementRef: ElementRef<HTMLElement>) {}

  name: string = "";

  iteration = 0;
  isAllergiesChecked: boolean = false;
  isIntolerancesChecked: boolean = false;
  isChronicConditionsChecked: boolean = false;
  isMedicationsChecked: boolean = false;
  isSurgeriesChecked: boolean = false;
  progressValue: number = 0;

  checked: boolean = false;
  gain: boolean = false;
  lose: boolean = false;
  maintain: boolean = false;

  strengthChecked: boolean = false;
  flexibilityChecked: boolean = false;
  enduranceChecked: boolean = false;


  setname(name: string) {
    this.name = name;
  }

  continue() {
      switch(this.iteration) {
      case 0:
        break;
      case 1:
        this.setname((<HTMLInputElement>document.getElementById("FullName")).value);
        break;
      case 2:

        break;
      case 3:
        break;
    }
    this.iteration++;
    this.progressValue = this.progressValue + 20;
  }

  handleClick() {
    console.log('which', this.checked);
    console.log('Improve Strength:', this.strengthChecked);
    console.log('Improve Flexibility:', this.flexibilityChecked);
    console.log('Improve Endurance:', this.enduranceChecked);
  }

  return() {
    this.iteration--;
    this.progressValue = this.progressValue - 20;
  }

}
