import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
    providedIn: 'root'
  })
  export class FitnessService {
    private apiUrl = 'http://localhost:5000/api'; // Replace with your API endpoint
  
    constructor(private http: HttpClient) { }
  
    sendHealthData(data: {
      weight_management: string,
      strength_goal: boolean,
      flexibility_goal: boolean,
      endurance_goal: boolean,
      height: number,
      weight: number,
      age: number,
      gender: string,
      occupation: string,
      sleep_hours: number,
      stress_level: string,
      smoking: boolean,
      diet_type: string,
      activity_level: string,
      allergies: string[],
      intolerances: string[],
      current_physical_health: string,
      chronic_conditions: string[],
      medications: string[],
      recent_surgeries_or_injuries: string[]
    }): Observable<any> {
      return this.http.post(this.apiUrl, data);
    }
  }
