import { Component } from '@angular/core';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.css']
})
export class FormComponent {
  iteration = 0;

  continue() {
    this.iteration++;
  }
  return() {
    this.iteration--;
  }
}
